<?php

namespace Rotex\RotexControlModule;

use Symfony\Component\HttpKernel\Bundle\Bundle;
use Symfony\Component\DependencyInjection\ContainerBuilder;
use Rotex\Sbp\CoreBundle\DependencyInjection\Compiler\MenuBuilderCompilerPass;

class RotexControlModule extends Bundle
{

    public function build(ContainerBuilder $container)
    {
        parent::build($container);

        $container->addCompilerPass(new MenuBuilderCompilerPass());
    }
}
